 * The settings file is used to specify which projects to include in your build.
 *
 * Detailed information about configuring a multi-project build in Gradle can be found
 * in the user manual at https://docs.gradle.org/8.1.1/userguide/multi_project_builds.html
 */

rootProject.name = "Prepared"

dependencyResolutionManagement {
	repositories {
		mavenCentral()
	}
}

pluginManagement {
	repositories {
		// region OpenSavvy Conventions

		maven {
			name = "opensavvy-gradle-conventions"
			url = uri("https://gitlab.com/api/v4/projects/51233470/packages/maven")

			metadataSources {
				gradleMetadata()
				mavenPom()
			}

			content {
				includeGroupAndSubgroups("dev.opensavvy")
			}
		}

		// endregion
		// region Standard repositories

		gradlePluginPortal()
		google()
		mavenCentral()

		// endregion
	}

	includeBuild("gradle/conventions")
}

plugins {
	id("dev.opensavvy.conventions.settings") version "1.5.1"
}

include(
	"gradle:templates:template-app",
	"gradle:templates:template-lib",

	"suite",

	"compat:compat-kotlinx-datetime",
	"compat:compat-java-time",
	"compat:compat-gradle",
	"compat:compat-filesystem",
	"compat:compat-arrow",
	"compat:compat-ktor",
	"compat:compat-parameterize",

	"runners:runner-kotlin-test",
	"runners:runner-kotest",
)
